import React from 'react';
import InputLine from './inputLine';
import SelectLine from './selectLine';
import TextareaLine from './textareaLine';
import OutputLine from './outputLine';
import { validateEmail, isEmpty } from './validations';

export default class RegisterForm extends React.Component {
  state = {
    registerData: {
      email: '',
      password: '',
      confirmation: '',
      age: '',
    },
    errors: {
      email: false,
      password: false,
      confirmation: false,
      age: false,
    }
  };
  onChange = (name, event) => {
    const value = event.target.value;
    const registerData = Object.assign({}, this.state.registerData);
    registerData[name] = value;
    this.setState({
      registerData
    });
  }

  validateConfirmation = (value, compareValue) => {
    if (this.isEmpty(value)) {
      return false;
    }

    if (value !== compareValue) {
      return false;
    }

    return true;
  }

  doRegister = (event) => {
    const {
      email,
      password,
      confirmation,
      age,
    } = this.state.registerData;

    const emailError = !validateEmail(email);
    const passwordError = isEmpty(password);
    const confirmationError = !this.validateConfirmation(confirmation, password);
    const ageError = isEmpty(age);

    this.setState({
      errors: {
        email: emailError,
        password: passwordError,
        confirmation: confirmationError,
        age: ageError,
      }
    });

    event.preventDefault();
  }

  render() {
    const {
      email,
      password,
      confirmation,
      age
    } = this.state.registerData;
    const { errors } = this.state;


  return(
    <article className="formulario">
    <form>

      <div className ="marco">

      <h6 className="nombreUsuario">
      Nombre de usuario
      </h6>

      <div className="nombreRegistro">

      <InputLine
      name="nombreRegistro"
      label="NombreRegistro"
      type="text"
      required={true}
      onChange={this.onChange}
      error={errors.nombre}
      value={nombre}
      placeholder="Ingrese su nombre"

      />
      </div>
      <h6 className="passUsuario">
      Contrase&ntilde;a
      </h6>

      <div>
      <InputLine className="passReg"
      name="password"
      label="PassReg"
      type="password"
      required={true}
      minLength="4"
      maxLength="8"
      onChange={this.onChange}
      error={errors.password}
      value={password}
      placeholder="Ingrese una contraseña"

      />
      </div>
     <h6 className="passUsuario2">
     Repite Contrase&ntilde;a
     </h6>
     <div>
     <InputLine className="passReg2"
     name="confirmation"
     label="ConfirmarContraseña"
     type="password"
     required={true}
     minLength="4"
     maxLength="8"
     onChange={this.onChange}
     error={errors.confirmation}
     value={confirmation}
     placeholder="Repita la contraseña"
     />
     </div>


      <h6 className="emailUsuario">
      Email
      </h6>
      <div className="emailReg">
      <InputLine
      name="emai"
      label="EmailReg"
      type="email"
      required={true}
      onChange={this.onChange}
      error={errors.email}
      value={email}
      placeholder="correo@ejemplo.cl"

      />

      <h6 className="edadlUsuario">
      Edad
      </h6>
      <InputLine className="edadUs"
      name="age"
      label="Edad"
      type="number"
      min="1"
      max="150"
      step="any"
      required={true}
      onChange={this.onChange}
      error={errors.age}
      value={age}
      />

      <button className="cPerfil"
      onClick={this.doRegister}>
      Crear Perfil
      </button>

      <Link to="/inicio">

      <button className="cancelar">
      Cancelar
      </button>

      < /Link>

      </div>
      </div>
      </form>
    </ article>
  );
}

}
