import React from 'react';
import {Link} from 'react-router-dom';
import InputLine from './inputLine';
import { especs } from '../api/indexespecs';

export default class EspecsForm extends React.Component {
  state = {
    registerData: {
      so: '',
      procesador: '',
      almacenamiento: '',
      memoria: '',
      grafica: '',
      direct: '',
    },
    errors: {
      so: false,
      procesador: false,
      almacenamiento: false,
      memoria: false,
      grafica: false,
      direct: false,
    }
  };

  doEspecs = (event) => {

    event.preventDefault();

    especs(this.state.especsData)


      .then((response) => {
        return response.text();
    })

      .then((token) => {
        localStorage.setItem('token', token);
        this.props.history.push('/inicio');
    });
  }
  onChange = (name, event) => {
    const value = event.target.value;
    const especsData = Object.assign({}, this.state.especsData);
    especsData[name] = value;
    this.setState({
      especsData
    });
  }

  render() {
  const {
      so,
      procesador,
      almacenamiento,
      memoria,
      grafica,
      direct
    } = this.state.especsData;
  const { errors } = this.state;


  return(

      <article>

        <h12>Caracteristicas de tu equipo</h12>
        <div className = "notificacion">
        <h13>Problema para ingresar las caracteristicas de tu equipo ? haz click aqui: </h13>
        <button className = "botonayuda">Ayuda</button>
        </div>

        <div className ="conjunto1">

        <div>Sistema operativo</div>

          <div className="ordenRegistro">
            <InputLine
                name="so"
                type="text"
                required={true}
                onChange={this.onChange}
                error={errors.so}
                value={so}
                placeholder="ej: windows 10"
            />

          </div>
        <div>Procesador</div>
          <div className="ordenRegistro">
            <InputLine
                name="procesador"
                type="text"
                required={true}
                onChange={this.onChange}
                error={errors.procesador}
                value={procesador}
                placeholder="Ej: intel i5, AMD, ... etc."
            />

          </div>
      <div>Almacenamineto libre</div>
        <div className="ordenRegistro">
           <InputLine
               name="almacenamiento"
               type="text"
               required={true}
               onChange={this.onChange}
               error={errors.almacenamiento}
               value={almacenamiento}
               placeholder="10Gb, 20GB, 50GB, ... etc."
            />

          </div>

      <div>Memoria RAM</div>
        <div className="ordenRegistro">
            <InputLine
                name="memoria"
                type="text"
                required={true}
                onChange={this.onChange}
                error={errors.memoria}
                value={memoria}
                placeholder="Ej: 2GB, 4GB, 8Gb, ... etc."
             />

          </div>

      <div>Tarjeta Grafica</div>
        <div className="ordenRegistro">
            <InputLine
                name="grafica"
                type="text"
                required={true}
                onChange={this.onChange}
                error={errors.grafica}
                value={grafica}
                placeholder="Ej:Intel, AMD, Nvidia 2GB, 4GB,.. etc."
            />
          </div>

      <div>Tarjeta Grafica</div>
        <div className="ordenRegistro">
            <InputLine
                name="grafica"
                type="text"
                required={true}
                onChange={this.onChange}
                error={errors.grafica}
                value={grafica}
                placeholder="Ej:Intel, AMD, Nvidia 2GB, 4GB,.. etc."
            />
            </div>

      <div>Direct</div>
        <div className="ordenRegistro">
            <InputLine
                name="direct"
                type="numbrer"
                required={true}
                onChange={this.direct}
                error={errors.grafica}
                value={direct}
                placeholder="Ej:7 , 8 ,9, 10"
            />

        </div>


        <Link to= "/ingresoEspecificacionesCompletado">

        <button className= "datospcusuario">
          Guardar
        </button>

      </Link>
      </div>

     </article>

    );
  }
}
