export function especs (usuario){

  var url = 'http://localhost:5000/users/37'
  var datos = {
  sistema: usuario.si_op,
  procesador: usuario.procesador,
  almacenamiento:usuario.almacenamiento,
  memoria:usuario.almacenamiento,
  grafica:usuario.confirmation
};

  return fetch( url , {
    method: 'PATCH',
    body : JSON.stringify( datos ),
    headers: {
      'Content-Type' : 'application/json'
    }
  });
}
