export function login(usuario){

  var url = 'http://localhost:5000/login'
  var datos = {
  correo: usuario.correo,
  password: usuario.password
};

  return fetch( url , {
    method: 'POST',
    body : JSON.stringify( datos ),
    headers: {
      'Content-Type' : 'application/json'
    }
  });
